import React from 'react';
//import NoImage from '../images/no_image.jpg';
// title={movie.Title}
//                 rating={movie.imdbRating}
//                 released={movie.Realeased}
//                 genre={movie.Genre}
//                 actor={movie.actors}
//                 plot={movie.Plot}
//                 boxoffice={movie.BoxOffice}

const MovieInfo = ({ image, title, plot, released, genre, rating, boxoffice }) => {
    return (
        <div className="card mb-3" style={{ maxWidth: '1920px' }}>
            <div className="row no-gutters">
                <div className="col-md-4">
                    <img src={image} width='300' className="card-img" alt="movieImage" />
                </div>
                <div className="col-md-8">
                    <div className="card-body">
                        <h2 className="card-title"><b>{title}</b></h2>
                        <h6 class="card-subtitle mb-2 text-muted">Genre: {genre}  </h6>
                        <h7 class="card-subtitle mb-2 text-muted">Released Date: {released}   </h7>
                        <p className="card-text">{plot}</p>
                        <p className="card-text"><small className="text-muted">Rating: {rating}, BoxOffice: {boxoffice}</small></p>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default MovieInfo
